import React, { Component } from 'react';
import { Card, CardHeader, CardBody, CardFooter, Button } from 'reactstrap';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import data from './_data';


function onAfterDeleteRow(rowKeys) {
  alert('The rowkey you drop: ' + rowKeys);
}

// If you want to enable deleteRow, you must enable row selection also.
const selectRowProp = {
  mode: 'checkbox'
};

class MeusBloqueios extends Component {

  constructor(props) {
    super(props);

    this.table = data.rows;
    this.options = {
      sortIndicator: true,
      hideSizePerPage: true,
      paginationSize: 3,
      hidePageListOnlyOnePage: true,
      clearSearch: true,
      alwaysShowAllBtns: false,
      withFirstAndLast: false
    }

    this.nbloqueios = "2048";

  }

  render() {
    return (
      <div className="animated fadeIn">
        <h1 class="text-primary">Meus Bloqueios!</h1>
        <Card>
          <CardHeader>
            <h2>Bloqueios - {this.nbloqueios}</h2> 
          </CardHeader>
          <CardBody>
          <BootstrapTable data={this.table} selectRow={ selectRowProp } version="4" striped hover pagination search options={this.options}>
              <TableHeaderColumn isKey dataField="idusuario" dataSort>ID</TableHeaderColumn>
              <TableHeaderColumn dataField="conta"  dataSort>CONTA</TableHeaderColumn>
              <TableHeaderColumn dataField="tipo" dataSort>TIPO</TableHeaderColumn>
              <TableHeaderColumn dataField="motivo" dataSort>MOTIVO</TableHeaderColumn>
            </BootstrapTable>
          </CardBody>
          <CardFooter>

          </CardFooter>

        </Card>

      </div>
    );
  }
}

export default MeusBloqueios;
