import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Button, Card, CardBody, Col, Container, Form, Input, Tolltip, InputGroup, InputGroupAddon, InputGroupText, Row } from 'reactstrap';
import logo from '../../../assets/img/brand/MeuML-logo2.png'



class RecuperarSenha extends Component {

  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      tooltipOpen: [false, false],
      tooltips: [
        {
          placement: 'top',
          text: 'Top',
        },
        {
          placement: 'bottom',
          text: 'Bottom',
        },
        {
          placement: 'left',
          text: 'Left',
        },
        {
          placement: 'right',
          text: 'Right',
        },
      ],
    };
  }

  toggle(i) {
    const newArray = this.state.tooltipOpen.map((element, index) => {
      return (index === i ? !element : false);
    });
    this.setState({
      tooltipOpen: newArray,
    });
  }

  render() {
    return (
      <div className="app flex-row align-items-center">
        <Container>
          <Row className="justify-content-center">
            <Col md="6">
              <Card class="col-md-12 text-muted py-5 d-md-down-none">
                  <CardBody className="text-center">
                    <div>
                      <h2><img src={logo} width="60%" class="espacoLogoCadastro" alt="MeuML" /></h2>
                      <Form>
                      <h2 class="tituloLogin">Recuperar Senha</h2>
                      <p class="alert alert-info fade show">Insira seu <b>EMAIL</b> que foi informado no momento de seu cadastro ou o ultimo email válido que estava vinculado a sua conta.</p>
                      <p class="alert alert-warning fade show">Será enviado para o <b>email informado abaixo</b> as instruções e informações de recuperação de senha.</p>
                      <InputGroup className="mb-3">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa fa-at"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input type="text" placeholder="Email" id="TooltipAviso" autoComplete="email" />
                        <Tooltip placement="right" isOpen={this.state.tooltipOpen[0]} target="TooltipAviso" toggle={() => {this.toggle(0);}}>
                          Enviaremos para este email as instruções de recuperação de senha, INFORME UM EMAIL VÁLIDO!
                        </Tooltip>
                      </InputGroup>
                      <Row>
                        <Col xs="12" className="text-center">
                        <Link to="/dashboard">
                          <Button color="primary" className="px-4">Enviar</Button>
                          </Link>
                        </Col>
                      </Row>
                    </Form>
                    </div>
                  </CardBody>
                </Card>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default RecuperarSenha;
