import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Button, Card, CardBody, CardGroup, Col, Container, Form, Input, InputGroup, InputGroupAddon, InputGroupText, Row, Redirect } from 'reactstrap';
import axios from 'axios';
import logo from '../../../assets/img/brand/MeuML-logo2.png'





class Login extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      auth: 'false',
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }
  handleSubmit(event) {
    //alert('Email: ' + this.state.email);
    //alert('Senha: ' + this.state.password);
    this.setState({auth: true});
    axios.post(`http://api.app2.meuml.com/api/auth/login`, {
      "email":"gabrielmouraodemelo@gmail.com",
      "password":"gabriel"
    })
    .then(res => {
        const token = res.data;
        this.setState({ token });
        const message = res.message;
        this.setState({ message });
        const status = res.status;
        this.setState({ status });
    });
    if (this.status === 'success'){
      this.setState({auth: 'true'});
      alert ('Resposta: ' + this.auth);
    }else{
      alert('Resposta erro: ' + this.auth);
    }


    //realizar os testes da API aqui (login)

    event.preventDefault();
  }

  render() {
    return (
      <div className="app flex-row align-items-center">
        <Container>
          <Row className="justify-content-center">
            <Col md="8">
              <CardGroup>
              <Card className="col-md-6 col-xm-12">
                  <CardBody className="text-center">
                    <div>
                      <h2><img src={logo} width="90%" className="espacoLogoCadastro" alt="MeuML" /></h2>
                      <p>Ainda não é cadastrado?</p>
                      <Link to="/cadastro">
                        <Button color="primary" className="px-4" tabIndex={-1}>Cadastre-se!</Button>
                      </Link>
                    </div>
                  </CardBody>
                </Card>
                <Card className="col-md-6 col-xm-12">
                  <CardBody>
                    <Form onSubmit={this.handleSubmit}>
                      <h2 className="tituloLogin">Acesse sua conta</h2>
                      <InputGroup className="mb-3">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="icon-envelope"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input type="text" name="email" value={this.state.email} placeholder="E-mail" onChange={this.handleInputChange} />
                      </InputGroup>
                      <InputGroup className="mb-4">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="icon-lock"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input type="password" name="password" value={this.state.password} placeholder="Senha" onChange={this.handleInputChange} />
                      </InputGroup>
                      <Row>
                        <Col xs="7">
                        <Link to="/recuperarsenha">
                        <Button color="outline-light" className="px-4">Recuperar senha</Button>
                        </Link>
                        </Col>
                        <Col xs="5" className="text-right">
                          <Input type="submit" value="Entrar" className="px-4 btn btn-primary" />
                        </Col>
                      </Row>
                    </Form>
                  </CardBody>
                </Card>

              </CardGroup>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Login;
