import React, { Component } from 'react';
import { Card, CardHeader, CardBody, CardFooter} from 'reactstrap';
import {BootstrapTable, TableHeaderColumn, ButtonGroup} from 'react-bootstrap-table';
import 'react-bootstrap-table/dist//react-bootstrap-table-all.min.css';
import data from './_data';



class MeusBloqueios extends Component {

  constructor(props) {
    super(props);

    this.table = data.rows;
    this.options = {
      sortIndicator: true,
      hideSizePerPage: true,
      paginationSize: 3,
      hidePageListOnlyOnePage: true,
      clearSearch: true,
      alwaysShowAllBtns: false,
      withFirstAndLast: false,
      btnGroup: this.createCustomButtonGroup
    }

    this.nbloqueios = "2048";

    // ...

    function acaodobotao(id) {
      alert('Ação do botão'+ id);
    }


  }

  createCustomButtonGroup = props => {
    return (
      <ButtonGroup className='my-custom-class' sizeClass='btn-group-md'>
        { props.showSelectedOnlyBtn }
        { props.exportCSVBtn }
        { props.insertBtn }
        { props.deleteBtn }
        <button type='button' className={ `btn btn-primary` } onClick={ this.acaodobotao('222') }>
          Desbloquear
        </button>
      </ButtonGroup>
    );
  }

  render() {

    const selectRow = {
      mode: 'checkbox'
    };

    return (
      <div className="animated fadeIn">
        <h1 className="text-primary">Meus Bloqueios!</h1>
        <Card>
          <CardHeader>
            <h2>Bloqueios - {this.nbloqueios}</h2> 
          </CardHeader>
          <CardBody>
          <BootstrapTable data={this.table} version="4" striped hover pagination search options={this.options} selectRow={ selectRow }>
              <TableHeaderColumn isKey dataField="idusuario" dataSort>ID</TableHeaderColumn>
              <TableHeaderColumn dataField="conta"  dataSort>CONTA</TableHeaderColumn>
              <TableHeaderColumn dataField="tipo" dataSort>TIPO</TableHeaderColumn>
              <TableHeaderColumn dataField="motivo" dataSort>MOTIVO</TableHeaderColumn>
            </BootstrapTable>
          </CardBody>
          <CardFooter>

          </CardFooter>

        </Card>

      </div>
    );
  }
}

export default MeusBloqueios;
