import React, { Component } from 'react';
import { Button, Card, CardBody, CardFooter, Col, Container, CustomInput, Form, Collapse, Input, InputGroup, InputGroupAddon, InputGroupText, Row } from 'reactstrap';
import logo from '../../../assets/img/brand/MeuML-logo2.png'
import axios from 'axios';


class Cadastro extends Component {

  constructor(props){
    super(props);

    this.toggle = this.toggle.bind(this);
    this.toggleFade = this.toggleFade.bind(this);
    this.state = {
      collapse: false,
      fadeIn: true,
      timeout: 300,
      userName: '',
      email: '',
      password: '',
      confirmPassword: '',
      termos: '',
      token: '',
      message: '',
      status: '',
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  toggle() {
    this.setState({ collapse: !this.state.collapse });
  }

  toggleFade() {
    this.setState((prevState) => { return { fadeIn: !prevState }});
  }

  handleSubmit(event) {

    event.preventDefault();

    axios.post(`https://api.app2.meuml.com/user`, {
      "email":this.state.email,
      "name":this.state.userName,
      "password":this.state.password
    })
    .then(res => {
      //console.log(res.data);
      const status = res.data.status;
      this.setState({status});
      const message = res.data.message;
      this.setState({message});
      const token = res.data.data.jwt;
      this.setState({token});
    })

    if (this.state.status === 'success'){
      this.setState({auth: 'true'});
      alert (this.state.message);
    }else{
      this.setState({auth: 'false'});
      alert('Não foi possível realizar o cadastro de sua conta, tente novamente!');
    }


    //realizar os testes da API aqui (login)
  }


  render() {
    return (
      <div className="app flex-row align-items-center">
        <Container>
          <Row className="justify-content-center">
            <Col md="9" lg="7" xl="6">
              <Card className="mx-4">
                <CardBody className="p-4">
                  <Form  onSubmit={this.handleSubmit}>
                    <img src={logo} width="80%" className="logoFormCadastro" alt="MeuML" />
                    <h2 className="text-center">Cadastro</h2>
                    <p className="alert alert-info text-center">Informe um e-mail válido e em uso, enviaremos um link de confirmação de cadastro para o e-mail informado abaixo.</p>
                    <InputGroup className="mb-3">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="icon-user"></i>
                        </InputGroupText>
                      </InputGroupAddon>
                      <Input type="text"
                                 name="userName"
                                 id="userName"
                                 placeholder="Usuário"
                                 autoComplete="username"
                                 required
                                 onChange={this.handleInputChange}
                                 value={this.state.userName} />
                    </InputGroup>
                    <InputGroup className="mb-3">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>@</InputGroupText>
                      </InputGroupAddon>
                      <Input type="email"
                                name="email"
                                 id="email"
                                 placeholder="E-mail"
                                 autoComplete="email"
                                 required
                                 onChange={this.handleInputChange}
                                 value={this.state.email} />
                    </InputGroup>
                    <InputGroup className="mb-3">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="icon-lock"></i>
                        </InputGroupText>
                      </InputGroupAddon>
                      <Input type="password"
                                     name="password"
                                     id="password"
                                     placeholder="Senha"
                                     autoComplete="new-password"
                                     required
                                     onChange={this.handleInputChange}
                                     value={this.state.password} />
                    </InputGroup>
                    <InputGroup className="mb-4">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="icon-lock"></i>
                        </InputGroupText>
                      </InputGroupAddon>
                      <Input type="password"
                                     name="confirmPassword"
                                     id="confirmPassword"
                                     placeholder="Confirmar Senha"
                                     autoComplete="new-password"
                                     required
                                     onChange={this.handleInputChange}
                                     value={this.state.confirmPassword} />
                    </InputGroup>
                    <Row>
                      <Col xs="12" sm="6">
                      <InputGroup>
                          <CustomInput
                            type="checkbox"
                            id="accept"
                            label="Eu aceito os Termos de Uso"
                            required
                            onChange={this.handleInputChange}>
                          </CustomInput>
                        </InputGroup>
                      </Col>
                      <Col xs="12" sm="6" className="text-right">
                      <Button type="submit" color="primary" disabled={this.state.submitButton}>Concluir Cadastro</Button>
                      </Col>
                    </Row>
                  </Form>
                </CardBody>
                <CardFooter className="p-4">
                <div className="card-footer-actions">
                <b>Termos de Uso</b> <Button className="card-footer-action badge badge-dark badge-pill float-right text-light" data-target="#collapseTermos" onClick={this.toggle}><i className="icon-arrow-down"></i></Button>
                </div>
                <Collapse isOpen={this.state.collapse} id="collapseTermos">
                  <Row>
                    <Col xs="12" sm="12">
                      <em>Termos de Uso</em>
                      Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt ut
                    laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation
                    ullamcorper suscipit lobortis nisl ut aliquip ex ea commodo consequat.
                    </Col>
                  </Row>
                  </Collapse>
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Cadastro;
